from Crypto.Util.number import *

import math
def inverse(a,p):
    u = a
    v = p
    x1 = 1
    x2 = 0
    while u != 1:
        q = math.floor(v/u)
        r = v - u*q
        x = x2 - x1*q
        v = u
        u = r
        x2 = x1
        x1 = x
    return (x1%p)  
def nhanbinhphuongcolap(a,k,n):
    b = 1
    if k == 0:
        return b
    A = a
    if k&1 == 1:
        b = a 
    for i in range (1, len(bin(k))-2):    
        A = (A**2)%n 
        if (k >> i)&1 == 1:
            b = (A*b) % n 
    return b 
def timp(p,q,e):
    phin = (1-p)*(1-q)
    d = inverse(e,phin)
    return d
def encryptRSA(m,e,n):
    c = nhanbinhphuongcolap(m,e,n)
    return c

def decryptRSA(c,d,n):
    m = nhanbinhphuongcolap(c,d,n)
    return m

